import React from "react";
import { useState, useMemo, useCallback,useEffect } from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import Bins from "./Bins";
import 'leaflet/dist/leaflet.css';
import { GeoJSON } from "react-leaflet";
import '../css/home.css';
import '../css/map.css';
import { render } from "@testing-library/react";


const overpass = require("query-overpass");

function Map() {
  
  const center = [50.049683,	19.944544]
  const zoom = 13
  const [lat, setLat] = useState();
  const [lng, setLng] = useState();
  let position = null;
    
  function DisplayPosition(props) {
    
    const [position, setPosition] = useState(() => props.map.getCenter())
    const onClick = useCallback(() => {
      // props.map.setView(center, zoom)
      props.setLat(position.lat)
      props.setLng(position.lng)
      console.log(lat)
    }, [props.map])
  
    const onMove = useCallback(() => {
      setPosition(props.map.getCenter())
    }, [props.map])
  
    useEffect(() => {
      props.map.on('move', onMove)
      return () => {
        props.map.off('move', onMove)
      }
    }, [props.map, onMove])
    return (
      <p>
        latitude: {position.lat.toFixed(4)}, longitude: {position.lng.toFixed(4)}{' '};
        <button onClick={onClick}>reset</button>
      </p>
    )

  }
  
  function Cords(position){
    const c = {position};
    console.log(c);
  }
  
  
  
  function ExternalStateExample(pso) {
    const [map, setMap] = useState(null)
    const displayMap = useMemo(
      () => (
        <MapContainer
        className="map"
          center={center}
          zoom={zoom}
          ref={setMap}>
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <Bins lat={lat}
            lng={lng}
          />
        </MapContainer>
      ),
      [],
    )
        
        
    return (
      <div>
        {map ? <DisplayPosition map={map} lat={lat} lng={lng} setLat={setLat} setLng={setLng}/> : null}
        {displayMap}
      </div>
    )
  }
  render(<ExternalStateExample/>)
}
export default Map;
